Module.Content:


#requireResource("com.atlassian.gadgets.publisher:ajs-gadgets")
#includeResources()

<script type="text/javascript">

    (function () {
        var gadget = AJS.Gadget({
            baseUrl: "__ATLASSIAN_BASE_URL__",
            useOauth: "/rest/gadget/1.0/currentUser",
            config: {
                descriptor : function (args){
                    var gadget = this;
                    return{
                        theme: "long-label",
                        fields: [
                            {
                                userpref: "Project",
                                class: "numField",
                                value: gadget.getPref("Project"),
                                label: gadget.getMsg("__MSG_issues.history.gadget.field.project.label__"),
                                description: gadget.getMsg("__MSG_issues.history.gadget.field.project.description__"),
                                type: "text"
                            },
                            {
                                userpref: "Issues",
                                class: "numField",
                                label: gadget.getMsg("__MSG_issues.history.gadget.field.issue.label__"),
                                description: gadget.getMsg("__MSG_issues.history.gadget.field.issue.description__"),
                                type: "text"
                            },
                            {
                                userpref: "Period",
                                label: gadget.getMsg("__MSG_issues.history.gadget.field.period.label__"),
                                description:gadget.getMsg("__MSG_issues.history.gadget.field.period.description__"),
                                type: "select",
                                options:[
                                    {
                                        label:gadget.getMsg("__MSG_issues.history.gadget.field.period.option.Hourly__")
                                    },
                                    {
                                        label:gadget.getMsg("__MSG_issues.history.gadget.field.period.option.Daily__")
                                    },
                                    {
                                        label:gadget.getMsg("__MSG_issues.history.gadget.field.period.option.Weekly__")
                                    },
                                    {
                                        label:gadget.getMsg("__MSG_issues.history.gadget.field.period.option.Monthly__")
                                    },
                                    {
                                        label:gadget.getMsg("__MSG_issues.history.gadget.field.period.option.Quarterly__")
                                    },
                                    {
                                        label:gadget.getMsg("__MSG_issues.history.gadget.field.period.option.Yearly__")
                                    }
                                ]
                            },
                            {
                                userpref: "Previously",
                                class: "numField",
                                label: gadget.getMsg("__MSG_issues.history.gadget.field.previously.label__"),
                                description: gadget.getMsg("__MSG_issues.history.gadget.field.previously.description__"),
                                type: "text"
                            },
                            AJS.gadget.fields.nowConfigured()
                        ]

                    }
                }
            },
            view: {
                enableReload: true,
                onResizeReload: true,
                template: function(args) {
                    var gadget = this;
                    var mainDiv = AJS.$("<div/>");
                    mainDiv.append(
                        AJS.$("<h1/>").text(args.user["fullName"])
                    );
                    mainDiv.append(
                        AJS.$("<h1/>").text(gadget.getMsg("__MSG_issues.history.gadget.headers.allIssues__"))
                    );
                    var issueList = AJS.$("<ul/>");
                    AJS.$(args.issuesData.issues).each(function() {
                        issueList.append(
                            AJS.$("<li/>").append(
                                AJS.$("<a/>").attr({
                                    target: "_parent",
                                    title: gadgets.util.escapeString(this.key),
                                    href: "__ATLASSIAN_BASE_URL__" + "/browse/" + this.key
                                }).text(this.id+ " " + this.key + " status:"+ this.statusName)
                            )
                        );
                    });
                    mainDiv.append(issueList);
                    mainDiv.append(
                        AJS.$("<h1/>").text(gadget.getMsg("__MSG_issues.history.gadget.headers.specificIssues__")+" "+gadget.getPref("Project"))
                    );
                    var requestedIssueList = AJS.$("<ul/>");
                    AJS.$(args.requestedIssuesData.issues).each(function() {
                        requestedIssueList.append(
                            AJS.$("<li/>").append(
                                AJS.$("<a/>").attr({
                                    target: "_parent",
                                    title: gadgets.util.escapeString(this.key),
                                    href: "__ATLASSIAN_BASE_URL__" + "/browse/" + this.key
                                }).text(this.id+ " " + this.key + " status:"+ this.statusName)
                            )
                        );
                    });
                    mainDiv.append(requestedIssueList);
                    gadget.getView().html(mainDiv);
                },
                args: [
                    {
                        key: "user",
                        ajaxOptions: function() {
                            return {
                                url: "/rest/gadget/1.0/currentUser"
                            };
                        }
                    },
                    {
                        key: "issuesData",
                        ajaxOptions: function() {
                            return {
                                url: "/rest/issueshistoryresource/1.0/issues.json"
                            };
                        }
                    },
                    {
                        key: "requestedIssuesData",
                        ajaxOptions:  function() {
                            return{
                                url: "/rest/issueshistoryresource/1.0/issues/byProjectName/" + encodeURI(gadget.getPref("Project"))
                            };
                        }
                    }
                ]
            }
        });
    })();

</script>
